import { ethers } from 'ethers';
import { CONTRACT_ADDRESS, CONTRACT_ABI } from '../contracts/BeamitAIGen1';
import { uploadToIPFS } from '../utils/ipfsHandler';

const AMOY_NETWORK = {
    chainId: '0x13882',
    chainName: 'Polygon Amoy Testnet',
    nativeCurrency: {
        name: 'MATIC',
        symbol: 'MATIC',
        decimals: 18
    },
    rpcUrls: ['https://rpc-amoy.polygon.technology/'],
    blockExplorerUrls: ['https://amoy.polygonscan.com/']
};

const NFTMinter = ({ glbUrl, originalNFT }) => {
    const switchToAmoy = async () => {
        try {
            await window.ethereum.request({
                method: 'wallet_switchEthereumChain',
                params: [{ chainId: AMOY_NETWORK.chainId }],
            });
        } catch (switchError) {
            if (switchError.code === 4902) {
                try {
                    await window.ethereum.request({
                        method: 'wallet_addEthereumChain',
                        params: [AMOY_NETWORK],
                    });
                } catch (addError) {
                    throw new Error('Failed to add Amoy network');
                }
            } else {
                throw switchError;
            }
        }
    };

    const mintNFT = async () => {
        try {
            if (!window.ethereum) {
                throw new Error('Please install MetaMask');
            }

            await switchToAmoy();
            await window.ethereum.request({ method: 'eth_requestAccounts' });

            const provider = new ethers.BrowserProvider(window.ethereum);
            const signer = await provider.getSigner();

            console.log('Starting IPFS upload...');
            const tokenURI = await uploadToIPFS(glbUrl, originalNFT);
            console.log('IPFS Upload complete:', tokenURI);

            const contract = new ethers.Contract(
                CONTRACT_ADDRESS,
                CONTRACT_ABI,
                signer
            );

            const userAddress = await signer.getAddress();

            // Simplified tokenId generation
            const tokenId = ethers.solidityPackedKeccak256(
                ["string", "address", "uint256"],
                [tokenURI, userAddress, Date.now()]
            );

            console.log('Minting with params:', {
                to: userAddress,
                uri: tokenURI,
                tokenId: tokenId,
                contract: CONTRACT_ADDRESS
            });

            // Add value for potential minting cost
            const tx = await contract.mint(
                userAddress,
                tokenURI,
                tokenId,
                "0x",
                {
                    gasLimit: 300000,
                    value: ethers.parseEther("0.01"), // Add some MATIC in case minting has a cost
                }
            );

            console.log('Transaction sent:', tx.hash);
            const receipt = await tx.wait();
            console.log('NFT minted successfully:', receipt);

            return receipt;

        } catch (error) {
            // More detailed error logging
            console.error('Minting error details:', {
                message: error.message,
                code: error.code,
                data: error.data,
                transaction: error.transaction,
                receipt: error.receipt
            });
            throw error;
        }
    };

    return (
        <div>
            <button 
                onClick={mintNFT}
                style={{
                    padding: '10px 20px',
                    backgroundColor: '#FF1493',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Mint NFT
            </button>
        </div>
    );
};

export default NFTMinter;
