import React, { useState } from 'react';
import NFTMinter from './components/NFTMinter';
import '@google/model-viewer';
import './App.css';

function App() {
  // Use the new test endpoint
  const testGlbUrl = "http://localhost:5001/test-glb";
  const [modelViewerError, setModelViewerError] = useState(null);

  return (
    <div className="App">
      <h1>Beamit AI NFT Minter Test</h1>

      {/* 3D Model Viewer */}
      <div style={{
        position: 'relative',
        width: '600px',
        height: '600px',
        margin: '0 auto'
      }}>
        {modelViewerError && (
          <div style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            color: '#FF1493',
            textAlign: 'center',
            padding: '20px',
            background: 'rgba(0,0,0,0.8)',
            borderRadius: '8px'
          }}>
            Error loading 3D model: {modelViewerError}
          </div>
        )}

        <model-viewer
          src={testGlbUrl}
          alt="3D Avatar"
          auto-rotate
          camera-controls
          camera-orbit="0deg 75deg 2.5m"
          min-camera-orbit="auto auto 1.5m"
          max-camera-orbit="auto auto 4m"
          camera-target="0m -0.2m 0m"
          bounds="tight"
          field-of-view="30deg"
          environment-image="neutral"
          shadow-intensity="1"
          exposure="1"
          shadow-softness="1"
          interaction-prompt="auto"
          auto-rotate-delay="0"
          rotation-per-second="30deg"
          min-field-of-view="25deg"
          max-field-of-view="45deg"
          interpolation-decay="200"
          loading="eager"
          reveal="auto"
          style={{
            width: '600px', 
            height: '600px', 
            backgroundColor: '#e0e0e0',
            border: '2px solid #FF1493',
            transform: 'translateY(-15%)',
            borderRadius: '8px'
          }}
          onLoad={() => {
            console.log('=== Model Viewer Loaded ===');
            console.log('Source URL:', testGlbUrl);
          }}
          onError={(error) => {
            console.error('Model viewer error:', error);
            setModelViewerError(error.detail);
          }}
        ></model-viewer>
      </div>

      {/* Debug GLB URL */}
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <p>Debug: GLB URL</p>
        <a 
          href={testGlbUrl} 
          target="_blank" 
          rel="noopener noreferrer"
          style={{ color: '#FF1493' }}
        >
          Test GLB File
        </a>
      </div>

      {/* NFT Minter */}
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <NFTMinter 
          glbUrl={testGlbUrl}
          originalNFT={{
            tokenId: "123",
            chainId: 80002
          }}
        />
      </div>
    </div>
  );
}

// Add the styles
const styles = `
.mint-button {
  background: linear-gradient(45deg, #FF1493, #FF69B4);
  color: white;
  font-weight: bold;
  padding: 12px 24px;
  border-radius: 25px;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.mint-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 15px rgba(255, 20, 147, 0.3);
}
`;

const styleSheet = document.createElement("style");
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);

export default App;
