import React, { useState, useEffect } from 'react';
import NFTMinter from './components/NFTMinter';
import './App.css';

function App() {
  // Hardcoded test data
  const testGlbUrl = "http://localhost:5001/proxy/glb"; // Your last working GLB URL
  const testNFT = {
    tokenId: "123",
    chainId: 80002  // Amoy testnet
  };

  return (
    <div className="App">
      <h1>Beamit AI NFT Minter Test</h1>

      {/* 3D Model Viewer */}
      <div className="model-viewer-container">
        <model-viewer
          src={testGlbUrl}
          alt="3D Avatar"
          camera-controls
          auto-rotate
          ar
          style={{
            width: '100%',
            height: '400px',
            backgroundColor: '#f5f5f5'
          }}
        >
        </model-viewer>
      </div>

      {/* NFT Minter */}
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <NFTMinter 
          glbUrl={testGlbUrl}
          originalNFT={testNFT}
        />
      </div>
    </div>
  );
}

export default App;
