import { ethers } from 'ethers';
import { CONTRACT_ADDRESS, CONTRACT_ABI } from '../contracts/BeamitAIGen1';
import { uploadToIPFS } from '../utils/ipfsHandler';

const AMOY_NETWORK = {
    chainId: '0x13882', // 80002 in hex
    chainName: 'Polygon Amoy Testnet',
    nativeCurrency: {
        name: 'MATIC',
        symbol: 'MATIC',
        decimals: 18
    },
    rpcUrls: ['https://rpc-amoy.polygon.technology/'],
    blockExplorerUrls: ['https://amoy.polygonscan.com/']
};

const NFTMinter = ({ glbUrl, originalNFT }) => {
    const switchToAmoy = async () => {
        try {
            await window.ethereum.request({
                method: 'wallet_switchEthereumChain',
                params: [{ chainId: AMOY_NETWORK.chainId }],
            });
        } catch (switchError) {
            if (switchError.code === 4902) {
                try {
                    await window.ethereum.request({
                        method: 'wallet_addEthereumChain',
                        params: [AMOY_NETWORK],
                    });
                } catch (addError) {
                    throw new Error('Failed to add Amoy network to MetaMask');
                }
            } else {
                throw switchError;
            }
        }
    };

    const mintNFT = async () => {
        try {
            if (!window.ethereum) {
                throw new Error('Please install MetaMask to mint NFTs!');
            }

            await switchToAmoy();
            await window.ethereum.request({ method: 'eth_requestAccounts' });

            const provider = new ethers.BrowserProvider(window.ethereum);
            const signer = await provider.getSigner();

            const network = await provider.getNetwork();
            if (network.chainId !== 80002n) {
                throw new Error('Please switch to Polygon Amoy Testnet');
            }

            console.log('Starting IPFS upload...');
            const tokenURI = await uploadToIPFS(glbUrl, originalNFT);
            console.log('IPFS Upload complete:', tokenURI);

            const contract = new ethers.Contract(
                CONTRACT_ADDRESS,
                CONTRACT_ABI,
                signer
            );

            // Get user's address
            const userAddress = await signer.getAddress();
            console.log('Minting to address:', userAddress);

            // Call the mint function directly
            const tx = await contract.mint(userAddress, tokenURI, {
                gasLimit: 500000  // Add gas limit to ensure transaction goes through
            });

            console.log('Minting transaction sent:', tx.hash);
            const receipt = await tx.wait();
            console.log('NFT minted successfully:', receipt);

        } catch (error) {
            console.error('Minting error:', error);
            throw error;
        }
    };

    return (
        <div>
            <button 
                onClick={mintNFT}
                style={{
                    padding: '10px 20px',
                    backgroundColor: '#FF1493',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Mint NFT
            </button>
        </div>
    );
};

export default NFTMinter;
