import React, { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import AvatarSelection from './components/AvatarSelection';
import ImageGallery from './components/ImageGallery';
import RenderResults from './components/RenderResults';
import { fetchLocalImages, generateImages, uploadImageToCloud } from './utils/api';
import './App.css';
import logo from './images/beamit-ai-logo.png';

console.log('Backend URL:', process.env.REACT_APP_API_URL);

function App() {
  const [selectedNFT, setSelectedNFT] = useState(null);
  const [generatedImages, setGeneratedImages] = useState([]);
  const [localImages, setLocalImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [pfpFile, setPfpFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [videoUrl, setVideoUrl] = useState('');
  const [fileName, setFileName] = useState('');
  const [account, setAccount] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [status, setStatus] = useState('Not connected');

  useEffect(() => {
    if (account) {
      fetchNFTs(account);
    }
  }, [account]);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);
        setStatus('Connected');

        const provider = new ethers.BrowserProvider(window.ethereum);
        const balance = await provider.getBalance(accounts[0]);
        console.log('ETH Balance:', ethers.formatEther(balance));

        // Fetch NFTs
        await fetchNFTs(accounts[0]);
      } catch (error) {
        console.error("Error connecting to MetaMask", error);
        setStatus('Connection failed');
      }
    } else {
      alert('Please install MetaMask!');
    }
  };

  const disconnectWallet = () => {
    setAccount(null);
    setNfts([]);
    setStatus('Not connected');
  };

  const fetchNFTs = async (walletAddress) => {
    try {
      const apiKey = process.env.REACT_APP_OPENSEA_API_KEY;
      console.log('Using OpenSea API Key:', apiKey);

      const chain = 'ethereum'; // or 'polygon', 'binance', etc., depending on the blockchain
      const response = await fetch(`https://api.opensea.io/api/v2/chain/${chain}/account/${walletAddress}/nfts`, {
        headers: {
          'X-API-KEY': apiKey,
          'accept': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('OpenSea API Response:', data);
      setNfts(data.nfts || []);
    } catch (error) {
      console.error("Error fetching NFTs", error);
    }
  };

  const handleCreateAvatar = async (imageUrl) => {
    setSelectedNFT(imageUrl);
    setGeneratedImages([]);
    try {
      const res = await fetch(imageUrl);
      const blob = await res.blob();
      const file = new File([blob], 'pfpImage.jpg', { type: blob.type });
      setPfpFile(file);
    } catch (error) {
      console.error('Error fetching image:', error);
    }
  };

  const handleGenerateImages = async () => {
    if (!selectedNFT || !pfpFile) {
      alert('Please select an image first.');
      return;
    }
    try {
      setLoading(true);
      const data = await generateImages(pfpFile);
      setGeneratedImages([data.imagePath]);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      alert(`Error generating images: ${error.message}`);
    }
  };

  const handleBeamit = async () => {
    if (!generatedImages.length) {
      alert('Please generate an image first.');
      return;
    }

    const fullBodyImage = generatedImages[0].split('/').pop();
    try {
      setUploadStatus('Uploading image to Google Cloud...');
      await uploadImageToCloud(fullBodyImage);
      setUploadStatus('Upload successful!');

      // Fetch the latest video URL after upload
      const response = await fetch(`${process.env.REACT_APP_API_URL}/get-latest-video`);
      const data = await response.json();

      if (response.ok && data.videoPath) {
        setVideoUrl(data.videoPath);
        setFileName(data.videoName);
      } else {
        console.error('Error fetching video:', data);
      }
    } catch (error) {
      setUploadStatus(`Upload failed: ${error.message}`);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} alt="Beamit AI Logo" style={{ maxWidth: '250px', marginLeft: '20px' }} />
        <div style={{ position: 'absolute', top: 20, right: 20, display: 'flex', gap: '10px' }}>
          <button onClick={connectWallet} className="wallet-button">
            {account ? 'Wallet Connected' : 'Connect Wallet'}
          </button>
          {account && <button onClick={disconnectWallet} className="wallet-button">Disconnect</button>}
        </div>
      </header>

      <div className="App-content">
        {videoUrl ? (
          <RenderResults videoUrl={videoUrl} fileName={fileName} />
        ) : (
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <AvatarSelection
              selectedNFT={selectedNFT}
              handleGenerateImages={handleGenerateImages}
              loading={loading}
            />
            <ImageGallery
              generatedImages={generatedImages}
              localImages={localImages}
              handleCreateAvatar={handleCreateAvatar}
              handleBeamit={handleBeamit}
              uploadStatus={uploadStatus}
            />
          </div>
        )}
        {account && (
          <div className="nft-gallery">
            {nfts.map((nft, index) => (
              <div key={index} className="nft-item">
                <img src={nft.image_url} alt={nft.name} style={{ width: '200px' }} />
                <p>{nft.name}</p>
                <button onClick={() => handleCreateAvatar(nft.image_url)} className="nft-button">Choose PFP</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
