import { NFTStorage } from 'nft.storage';

const NFT_STORAGE_KEY = process.env.REACT_APP_NFT_STORAGE_KEY;
const client = new NFTStorage({ token: NFT_STORAGE_KEY });

export const uploadToIPFS = async (glbUrl, originalNFT) => {
    try {
        console.log('Downloading GLB file...');
        const response = await fetch(glbUrl);
        const glbBlob = await response.blob();

        // Upload GLB to IPFS
        console.log('Uploading GLB to IPFS...');
        const glbCid = await client.storeBlob(glbBlob);
        const glbIpfsUrl = `ipfs://${glbCid}`;

        // Create metadata
        const metadata = {
            name: `Beamit AI Avatar #${Date.now()}`,
            description: "AI-Generated 3D Avatar from Beamit",
            image: glbIpfsUrl,
            animation_url: glbIpfsUrl,
            attributes: [
                {
                    trait_type: "Original NFT ID",
                    value: originalNFT.tokenId
                },
                {
                    trait_type: "Original Chain ID",
                    value: originalNFT.chainId
                }
            ]
        };

        // Upload metadata to IPFS
        console.log('Uploading metadata to IPFS...');
        const metadataBlob = new Blob([JSON.stringify(metadata)], { type: 'application/json' });
        const metadataCid = await client.storeBlob(metadataBlob);

        return `ipfs://${metadataCid}`;
    } catch (error) {
        console.error('IPFS upload error:', error);
        throw error;
    }
};
