import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import './App.css';

function App() {
  const mountRef = useRef(null);

  useEffect(() => {
    // Scene setup
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0xffffff);

    // Camera setup
    const camera = new THREE.PerspectiveCamera(
      75,
      mountRef.current.clientWidth / mountRef.current.clientHeight,
      0.1,
      1000
    );
    camera.position.z = 2;

    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(
      mountRef.current.clientWidth,
      mountRef.current.clientHeight
    );
    mountRef.current.appendChild(renderer.domElement);

    // Light setup
    const light = new THREE.AmbientLight(0xffffff, 1);
    scene.add(light);

    // GLTF Loader
    const loader = new GLTFLoader();
    const glbUrl = 'https://storage.googleapis.com/fullbody-images/dg-results/avatar6.glb'; // Replace with your GLB URL

    loader.load(
      glbUrl,
      (gltf) => {
        scene.add(gltf.scene);
      },
      undefined,
      (error) => {
        console.error('An error happened while loading the GLB file:', error);
      }
    );

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      scene.traverse((object) => {
        if (object.isMesh) {
          object.rotation.y += 0.005;
        }
      });
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup on unmount
    return () => {
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h2>Your 3D Avatar</h2>
      </header>
      <div className="content">
        <div
          ref={mountRef}
          style={{ width: '600px', height: '600px', margin: 'auto' }}
        />
        <button className="mint-button">Mint NFT</button>
      </div>
    </div>
  );
}

export default App;
